int countGCDSubarrays(vector<int> &numbers, int targetGCD)
{
    int subarrayCount= 0;

    for(int startIndex= 0; startIndex < numbers.size(); ++startIndex)
    {
        int currentGCD= 0;
        for(int endIndex= startIndex; endIndex < numbers.size(); ++endIndex)
        {
            currentGCD= computeGCD(currentGCD, numbers[endIndex]);
            if(currentGCD == targetGCD)
                ++subarrayCount;
        }
    }

    return subarrayCount;
}

