long long minCost(vector<int> &numList, vector<int> &costList)
{
    long long minimumCost= LLONG_MAX;
    long long baseValue= *min_element(numList.begin(), numList.end());

    for(int targetValue= baseValue; targetValue < baseValue + 2; targetValue++)
    {
        long long localResult= 0;
        for(int index= 0; index < numList.size(); index++)
        {
            localResult+= abs(numList[index] - targetValue) * costList[index];
        }
        minimumCost= min(minimumCost, localResult);
    }

    return minimumCost;
}

