int minOperations(vector<int> &numList, vector<int> &targetList)
{
    int length= numList.size();
    int totalNumSum= 0;
    int totalTargetSum= 0;
    for(int index= 0; index < length; index++)
    {
        totalNumSum+= numList[index];
        totalTargetSum+= targetList[index];
    }
    int difference= (totalTargetSum - totalNumSum) / 2;
    int operationCount= 0;
    for(int index= 0; index < length; index++)
    {
        if(numList[index] > targetList[index])
        {
            operationCount+= (numList[index] - targetList[index] - difference) / 2;
        }
    }
    return operationCount;
}

