#include <bits/stdc++.h>
#define mod 1000000007
using namespace std;

int distinctStrings(string inputString, int substringLength)
{
    int stringLength= inputString.length(), uniqueCount= 0;
    unordered_set<string> uniqueStringsSet;

    for(int i= 0; i <= stringLength - substringLength; ++i)
    {
        string modifiedString= inputString.substr(0, i) + inputString.substr(i + substringLength);
        if(uniqueStringsSet.count(modifiedString) == 0)
        {
            uniqueStringsSet.insert(modifiedString);
            uniqueCount= (uniqueCount + 1) % mod;
        }
    }
    return uniqueCount + 1; // Add 1 for the original string
}

